mapper.select:
select count(*)
from project
where
    project_key = #{projectKey,jdbcType=VARCHAR}
  AND deleted = #{deleted,jdbcType=BOOLEAN}

select count(*)
from project
where id = #{id,jdbcType=NUMERIC}
  AND owner_id = #{ownerId,jdbcType=NUMERIC}

select count(*)
from project
where team_id = #{teamId,jdbcType=NUMERIC}
  AND deleted = #{deleted,jdbcType=BOOLEAN}

select p.*, al.time as last_activity_time from project as p inner join activity_log as al on p.id = al.item_id
al.action = 1 and p.deleted = false
and ( p.name like concat('%', #{keyword}, '%') or p.project_key like concat('%', #{keyword}, '%') )
and al.user_id = #{userId}
and al.time = ( select max(time) from activity_log where item_id = al.item_id and action = 1
and user_id = #{userId}
)
order by al.time desc
